import React from 'react';

import '../App.css';
import Buttonz from '../props/Buttonz';

class Formz extends React.Component {

    constructor(props) {
    super(props);    this.state = { 

    }
}

    weightChange = (weightValue) => {
        this.setState({ weight : weightValue.target.value });
        }



    heightChange = (heightValue) => {
        this.setState({ height:  heightValue.target.value });
        console.log(heightValue.target.value)
    }


    render() {
        return (
            <div class="input-group">
                <form>
                    <label>
                        <h2 className="h2-style">BMI CALCULATOR</h2>
                        <p> &emsp;  <input type="text" placeholder="NAME" name="name" /> </p>
                        <p> &emsp;  <input type="text" placeholder="AGE" name="age" /> &ensp; years </p>
                        <p> &emsp;  <input type="text" placeholder="HEIGHT" name="height" onChange={this.heightChange}/> &ensp; m </p>
                        <p> &emsp;  <input type="text" placeholder="WEIGHT" name="weight" onChange={this.weightChange}/> &ensp; kg</p><br></br>
                        <Buttonz label="SUBMIT" onClick={ this.computeBmi } />



                    <div className="row">
                    <script>

          if(this.state.bmiValue != 0)?null:<h3>Your BMI = {this.state.bmiValue}</h3> 
             </script>


      </div>
      <div>
        <h3>{this.state.bmiClass}</h3>
      </div>
      <h1>End </h1>   
      </label>
                    </form>     
      </div>

    )
    }
    computeBmi = () => {

        console.log("wgt : " +this.state.weight);

        let bmiValue = ( this.state.weight / (this.state.height * this.state.height));
        console.log("bmiValue : " +bmiValue);
        this.setState({ bmi : bmiValue });
        let bmiClass = this.getBmi(bmiValue);
        this.setState({ bmiClass : bmiClass });
        console.log("bmiValue : " +bmiValue);
        console.log("bmiClass : " +bmiClass);
    }

    getBmi(bmi) {
        if(bmi < 18.5) {
            return "Underweight";
        }
        if(bmi >= 18.5 && bmi < 24.9) {
            return "Normal weight";
        }
        if(bmi >= 25 && bmi < 29.9) {
            return "Overweight";
        }
        if(bmi >= 30) {
            return "Obesity";
        }
    }
}



export default Formz;
