import React from 'react';

import '../App.css';
import Buttonz from '../props/Buttonz';

class Formz extends React.Component {

    constructor(props) {
        super(props);
        this.state = {

        }
    }

    weightChange = (weightValue) => {
        this.setState({ weight: weightValue.target.value });
    }



    heightChange = (heightValue) => {
        this.setState({ height: heightValue.target.value });
        console.log(heightValue.target.value)
    }


    render() {
        return (
            <div>

                <label>
                    <h2 className="h2-style">BMI CALCULATOR</h2>
                    <p> &emsp;  <input type="text" placeholder="Name" name="name" size="25" /> </p>
                    <p> &emsp;  <input type="text" placeholder="Age" name="age" size="25"/> &ensp; years </p>
                    <p> &emsp;  <input type="text" placeholder="Enter your height" name="height" onChange={this.heightChange} size="25"/> &ensp; m </p>
                    <p> &emsp;  <input type="text" placeholder="Enter your weight" name="weight" onChange={this.weightChange} size="25" /> &ensp; kg</p>
                    <Buttonz label="SUBMIT" onClick={this.computeBmi} /><br></br>
                    <div>
                        {
                            isNaN(this.state.bmi) ? null : <p>Your BMI = {this.state.bmi}</p>
                        }

                    </div>
                    <h4 style={{ color: 'red' }} >{this.state.bmiClass}</h4>
                </label>
            </div>

        )
    }
    computeBmi = () => {

        console.log("wgt : " + this.state.weight);

        let bmiValue = (this.state.weight / (this.state.height * this.state.height));
        console.log("bmiValue : " + bmiValue);
        this.setState({ bmi: bmiValue });
        let bmiClass = this.getBmi(bmiValue);
        this.setState({ bmiClass: bmiClass });
        this.setState({ weight: "" });
        this.setState({ height: "" });
        console.log("bmiValue : " + bmiValue);
        console.log("bmiClass : " + bmiClass);
    }

    getBmi(bmi) {
        if (bmi < 18.5) {
            return "Underweight";
        }
        if (bmi >= 18.5 && bmi < 24.9) {
            return "Normal weight";
        }
        if (bmi >= 25 && bmi < 29.9) {
            return "Overweight";
        }
        if (bmi >= 30) {
            return "Obesity";
        }
    }
}



export default Formz;
